SELECT (?rel AS <Account>)
    WHERE {
      {
        SELECT ?rel
        WHERE {
          ?o a pwc:AccountingTransaction;
            pwc:relatedTo ?rel.
          ?rel a pwc:Account.
        }
        GROUP BY ?rel
        HAVING (COUNT(?o) < ?count)
      }
}

SELECT (?doc AS <Document>) (SUM(?amount) AS ?result)
WHERE {
  ?doc a pwc:Document;
       pwc:number ?docNumber;
       pwc:partOf ?t.

  ?t a pwc:AccountingTransaction;
     pwc:amount ?amount;
     pwc:relatedTo ?acc.

  ?acc a pwc:Account;
       pwc:number ?accNumber.

  FILTER (STRSTARTS(?accNumber, '?acc_num') = true)
}
GROUP BY ?doc

